mapper.select:
select * from teacher t, dept d
t.tno = #{tno,jdbcType=VARCHAR}
and d.dno=t.tno

mapper.insert:
insert into teacher (tno, name, pwd,
birthday, dno, mobile,
photo, type, remark
)
values (#{tno,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR},
#{pwd,jdbcType=VARCHAR},
#{birthday,jdbcType=VARCHAR}, #{dno,jdbcType=VARCHAR}, #{mobile,jdbcType=VARCHAR},
#{photo,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR},
#{remark,jdbcType=VARCHAR}
)

mapper.select:
select * from teacher t,dept d



		name = #{name,jdbcType=VARCHAR}


		and pwd = #{pwd,jdbcType=VARCHAR}


		and birthday = #{birthday,jdbcType=VARCHAR}


		and dno = #{dno,jdbcType=VARCHAR}


		and mobile = #{mobile,jdbcType=VARCHAR}


		and photo = #{photo,jdbcType=VARCHAR}


		and type = #{type,jdbcType=VARCHAR}


		and remark = #{remark,jdbcType=VARCHAR}


	and t.dno = d.dno
