mapper.select:
select 

from annuabonus
where tno = #{tno,jdbcType=VARCHAR}
  and year = #{year,jdbcType=VARCHAR}

mapper.delete:
delete from annuabonus
where tno = #{tno,jdbcType=VARCHAR}
  and year = #{year,jdbcType=VARCHAR}

mapper.insert:
insert into annuabonus (tno, year, name, 
  paperReward, treatiseReward, patentReward, 
  topicReward, awardsReward, reamrk
  )
values (#{tno,jdbcType=VARCHAR}, #{year,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{paperreward,jdbcType=INTEGER}, #{treatisereward,jdbcType=INTEGER}, #{patentreward,jdbcType=INTEGER}, 
  #{topicreward,jdbcType=INTEGER}, #{awardsreward,jdbcType=INTEGER}, #{reamrk,jdbcType=VARCHAR}
  )

mapper.update:
update annuabonus
set name = #{name,jdbcType=VARCHAR},
  paperReward = #{paperreward,jdbcType=INTEGER},
  treatiseReward = #{treatisereward,jdbcType=INTEGER},
  patentReward = #{patentreward,jdbcType=INTEGER},
  topicReward = #{topicreward,jdbcType=INTEGER},
  awardsReward = #{awardsreward,jdbcType=INTEGER},
  reamrk = #{reamrk,jdbcType=VARCHAR}
where tno = #{tno,jdbcType=VARCHAR}
  and year = #{year,jdbcType=VARCHAR}
