mapper.select:
select * from topic,teacher



			dno = #{dno,jdbcType=VARCHAR}


			and tno = #{tno,jdbcType=VARCHAR}


			and author = #{author,jdbcType=VARCHAR}


			and projectUnits = #{projectunits,jdbcType=VARCHAR}


			and topicname = #{topicname,jdbcType=VARCHAR}


			and startDate = #{startdate,jdbcType=VARCHAR}


			and endDate = #{enddate,jdbcType=VARCHAR}


			and evolve = #{evolve,jdbcType=VARCHAR}


			and adjunct = #{adjunct,jdbcType=VARCHAR}


			and bounty = #{bounty,jdbcType=INTEGER}


			and firstTrial = #{firsttrial,jdbcType=VARCHAR}


			and review = #{review,jdbcType=VARCHAR}


			and ramark = #{ramark,jdbcType=VARCHAR}


	and topic.tno=teacher.tno
